export const cartReducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case "ADD_TO_CART":
      return { ...state, cartList: payload };

    case "REMOVE_FROM_CART":
      return;

    case "UPDATE_TOTAL":
      return;

    default:
      throw new Error("No Case Found In Reducer");
  }
};
